mapper.update:
update CHANNEL_ORDER_PRE_CHECK

 
     
          
           when ORDER_SEQ_NBR=#{item.orderSeqNbr} then #{item.checkStatus}
          
     
  
  
     
          
           when ORDER_SEQ_NBR=#{item.orderSeqNbr} then #{item.downOrderStatus}
          
     
  
 
where ORDER_SEQ_NBR in

     #{item.orderSeqNbr,jdbcType=VARCHAR}

mapper.select:
    select count(*) as TOTAL_COUNT from CHANNEL_ORDER_PRE_CHECK
    where CHANNEL_CODE=#{channelCode,jdbcType=VARCHAR}
    and TRANS_DATE=#{checkDate,jdbcType=DATE}
     union all
    select count(*) as SUCCESS_COUNT from CHANNEL_ORDER_PRE_CHECK
    where CHANNEL_CODE=#{channelCode,jdbcType=VARCHAR}
    and TRANS_DATE=#{checkDate,jdbcType=DATE}
    and ORDER_STATUS='0'
    and CHECK_STATUS='2'
     union all
    select count(*) as FAIL_COUNT from CHANNEL_ORDER_PRE_CHECK
    where CHANNEL_CODE=#{channelCode,jdbcType=VARCHAR}
    and TRANS_DATE=#{checkDate,jdbcType=DATE}
    and ORDER_STATUS='1'
    and CHECK_STATUS='2'
     union all
    select count(*) as FAIL_COUNT from CHANNEL_ORDER_PRE_CHECK
    where CHANNEL_CODE=#{channelCode,jdbcType=VARCHAR}
    and TRANS_DATE=#{checkDate,jdbcType=DATE}
    and ORDER_STATUS='1'
    and CHECK_STATUS='3'
     union all
    select sum(TRANS_AMT) as TOTAL_AMT from CHANNEL_ORDER_PRE_CHECK
    where CHANNEL_CODE=#{channelCode,jdbcType=VARCHAR}
    and TRANS_DATE=#{checkDate,jdbcType=DATE}
	 union all
    select sum(TRANS_AMT) as SUCCESS_AMT from CHANNEL_ORDER_PRE_CHECK
    where CHANNEL_CODE=#{channelCode,jdbcType=VARCHAR}
    and TRANS_DATE=#{checkDate,jdbcType=DATE}
    and ORDER_STATUS='0'
    and CHECK_STATUS='2'    
     union all
    select sum(TRANS_AMT) as FAIL_AMT from CHANNEL_ORDER_PRE_CHECK
    where CHANNEL_CODE=#{channelCode,jdbcType=VARCHAR}
    and TRANS_DATE=#{checkDate,jdbcType=DATE}
    and ORDER_STATUS='1'
    and CHECK_STATUS='2' 
     union all
    select sum(TRANS_AMT) as UNCHECK_AMT from CHANNEL_ORDER_PRE_CHECK
    where CHANNEL_CODE=#{channelCode,jdbcType=VARCHAR}
    and TRANS_DATE=#{checkDate,jdbcType=DATE}
    and ORDER_STATUS='1'
    and CHECK_STATUS='3'
